INSERT INTO `tl_content` (`id`, `pid`, `sorting`, `tstamp`, `type`, `headline`, `text`, `addImage`, `singleSRC`, `multiSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `perRow`, `listtype`, `listitems`, `tableitems`, `summary`, `thead`, `tfoot`, `url`, `linkTitle`, `embed`, `target`, `module`, `space`, `align`, `cssID`, `form`, `code`, `cteAlias`, `caption`, `html`, `mooHeadline`, `mooStyle`, `mooClasses`, `mooType`, `sortable`, `invisible`, `imageUrl`, `useImage`, `sortBy`, `highlight`, `protected`, `groups`, `guests`, `com_template`, `com_perPage`, `com_order`, `com_moderate`, `com_bbcode`, `com_disableCaptcha`, `useHomeDir`, `article`, `perPage`, `dfTitle`, `dfSize`, `dfInterval`, `dfPause`, `sortIndex`, `sortOrder`, `dfTemplate`) VALUES(23, 17, 128, 1195842858, 'text', 'a:2:{s:4:"unit";s:2:"h1";s:5:"value";s:21:"Why do we need roles?";}', 'Imagine that you and a friend of yours are managing a website. Maybe you are a successful web designer whereas your friend has no idea about CSS but is a committed writer. Therefor, you have decided to share the work. In the future, you will be doing the design and administration of the website and your friend will be writing articles. This is a basic principle of any CMS by the way - separation of content and design.<br />\n<br />\nNow, as both of you have to have access to the back end, your friend would still be able to edit the look of the website or to create new user accounts, although he is actually not responsible for design or administration. Therefor, you want to disable these functions for his account. At this point, you have created a role (although it exists in your mind only) that you could name "editor" or "author".<br />\n<br />\nTo learn how user permissions can be restricted, let''s take a closer look at the roles of our example users.\n', '', '', '', '', '', '', '', '', 4, '', '', '', '', '', '', '', '', '', '', 0, 'a:2:{i:0;s:0:"";i:1;s:2:"16";}', '', 'a:2:{i:0;s:0:"";i:1;s:0:"";}', 0, '', 0, '', '', '', '', '', '', '', '', '', '', '', '', '', NULL, '', '', 0, '', '', '', '', '', 0, 0, '', '', 7, '', 0, '', '');
