INSERT INTO `tl_content` (`id`, `pid`, `sorting`, `tstamp`, `type`, `headline`, `text`, `addImage`, `singleSRC`, `multiSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `perRow`, `listtype`, `listitems`, `tableitems`, `summary`, `thead`, `tfoot`, `url`, `linkTitle`, `embed`, `target`, `module`, `space`, `align`, `cssID`, `form`, `code`, `cteAlias`, `caption`, `html`, `mooHeadline`, `mooStyle`, `mooClasses`, `mooType`, `sortable`, `invisible`, `imageUrl`, `useImage`, `sortBy`, `highlight`, `protected`, `groups`, `guests`, `com_template`, `com_perPage`, `com_order`, `com_moderate`, `com_bbcode`, `com_disableCaptcha`, `useHomeDir`, `article`, `perPage`, `dfTitle`, `dfSize`, `dfInterval`, `dfPause`, `sortIndex`, `sortOrder`, `dfTemplate`) VALUES(46, 1, 896, 1166780419, 'text', 'Roles and permissions', 'On the next page (<em>The academy</em>) we will take a closer look at the different roles and we will find out what our users are allowed to do and what they cannot do. You can then take a closer look at how these access restrictions have been implemented in the back end.<br />\n<br />\nMake sure to visit <a href="http://www.typolight.org/" title="Visit typolight.org" onclick="window.open(this.href); return false;">http://www.typolight.org</a> for more information.\n', '', '', '', '', '', '', '', '', 4, '', '', '', '', '', '', '', '', '', '', 0, 'a:2:{i:0;s:2:"24";i:1;s:0:"";}', '', '', 0, '', 0, '', '', '', '', '', '', '', '', '', '', '', '', '', NULL, '', '', 0, '', '', '', '', '', 0, 0, '', '', 7, '', 0, '', '');
INSERT INTO `tl_content` (`id`, `pid`, `sorting`, `tstamp`, `type`, `headline`, `text`, `addImage`, `singleSRC`, `multiSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `perRow`, `listtype`, `listitems`, `tableitems`, `summary`, `thead`, `tfoot`, `url`, `linkTitle`, `embed`, `target`, `module`, `space`, `align`, `cssID`, `form`, `code`, `cteAlias`, `caption`, `html`, `mooHeadline`, `mooStyle`, `mooClasses`, `mooType`, `sortable`, `invisible`, `imageUrl`, `useImage`, `sortBy`, `highlight`, `protected`, `groups`, `guests`, `com_template`, `com_perPage`, `com_order`, `com_moderate`, `com_bbcode`, `com_disableCaptcha`, `useHomeDir`, `article`, `perPage`, `dfTitle`, `dfSize`, `dfInterval`, `dfPause`, `sortIndex`, `sortOrder`, `dfTemplate`) VALUES(28, 17, 352, 1195909041, 'text', 'a:2:{s:4:"unit";s:2:"h1";s:5:"value";s:0:"";}', 'TYPOlight is a tree-based content management system, meaning that each front end page refers to a page in the hierarchically ordered site structure. Administrators can always see the whole page tree, whereas users, by default, do not have access to it at all. To enable one or more pages for a user, you have to "mount" them into his profile. The same applies to the file manager and the folders of the upload directory.<br />\n<br />\nSince we want Mrs. Lewis to be able to edit the courses page and the page of her own class, I have choosen page <em>Courses</em> as mounting point (subpages are included automatically). If you log in to the back end as Mrs. Lewis, you will only see those three pages in the site structure.\n', '1', 'tl_files/music_academy/pagemounts.gif', '', 'a:2:{i:0;s:0:"";i:1;s:0:"";}', 'Pagemounts', 'below', 'a:5:{s:6:"bottom";s:0:"";s:4:"left";s:0:"";s:5:"right";s:0:"";s:3:"top";s:2:"16";s:4:"unit";s:2:"px";}', '', 4, '', '', '', '', '', '', '', '', '', '', 0, 'a:2:{i:0;s:2:"24";i:1;s:0:"";}', '', 'a:2:{i:0;s:0:"";i:1;s:0:"";}', 0, '', 0, '', '', '', '', '', '', '', '', '', '', '', '', '', NULL, '', '', 0, '', '', '', '',