INSERT INTO `tl_news` (`id`, `pid`, `tstamp`, `date`, `alias`, `author`, `published`, `headline`, `teaser`, `subheadline`, `text`, `addImage`, `singleSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `source`, `jumpTo`, `url`, `target`, `caption`, `time`, `addEnclosure`, `enclosure`, `noComments`) VALUES(1, 1, 1195909329, 1193473800, 'james-wilson-returns', '1', '1', 'Associate Professor James Wilson returns', 'Jazz pianist James Wilson returns to Music Academy to give lessons in traditional and contemporary Jazz Piano.', 'Veteran Jazz pianist James Wilson has enjoyed international renown since graduating from Music Academy in 1968.', 'James Wilson and the stage have been lifelong partners. The jazz pianist\nand youngest son of a preacher began playing in his father''s church as\na preschooler, took his first solo at the age of six, and has been a\nlead performer ever since.\n', '1', 'tl_files/music_academy/james-wilson.jpg', 'a:2:{i:0;s:3:"120";i:1;s:0:"";}', 'James Wilson', 'left', 'a:5:{s:6:"bottom";s:0:"";s:4:"left";s:0:"";s:5:"right";s:1:"3";s:3:"top";s:1:"3";s:4:"unit";s:2:"px";}', '1', 'default', 13, '', '', '', 1193473800, '', '', '');
INSERT INTO `tl_news` (`id`, `pid`, `tstamp`, `date`, `alias`, `author`, `published`, `headline`, `teaser`, `subheadline`, `text`, `addImage`, `singleSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `source`, `jumpTo`, `url`, `target`, `caption`, `time`, `addEnclosure`, `enclosure`, `noComments`) VALUES(2, 1, 1195909909, 1184940000, 'scholarship-program', '1', '1', 'Scholarship Program', 'Music Academy debuts a new Scholarship Program that will cover all tuition and housing fees.', 'Music Academy debuts a new Scholarship Program.', 'In a major new initiative to attract the best young musicians,\nregardless of their ability to pay, Music Academy has announced a\nScholarship Program that will cover all tuition and housing fees. Scholars will attend Music Academy\nfor four years, with all fees provided at no charge.\n', '', '', '', '', '', '', '', 'default', 13, '', '', '', 1184940000, '', '', '');
INSERT INTO `tl_news` (`id`, `pid`, `tstamp`, `date`, `alias`, `author`, `published`, `headline`, `teaser`, `subheadline`, `text`, `addImage`, `singleSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `source`, `jumpTo`, `url`, `target`, `caption`, `time`, `addEnclosure`, `enclosure`, `noComments`) VALUES(3, 1, 1195909951, 1188285300, 'open-days', '3', '1', 'Open days', 'Every friday in September the gates of the Music Academy''s campus will be thrown wide open to welcome everyone for a day filled with fun and interest.', 'Welcome everybody to Music Academy', 'Every friday in September 2006 the gates of the Music Academy''s campus will be thrown wide open to welcome everyone for a day filled with fun and interest. The programme includes: <br />\n<br />\n<strong>\nShowcase events</strong>: demonstrations and\nactivities organised by each of the academic units<br />\n<strong>Courses Information</strong>: advice about studying and details of MA courses<br />\n<strong>Lectures</strong>: a series of illustrated talks on interesting and topical\nissues<br />\n<strong>Entertainment</strong>: campus tour, music, activities and buffet\n', '', '', '', '', '', '', '', 'default', 13, '', '', '', 1188285300, '', '', '');
INSERT INTO `tl_news` (`id`, `pid`, `tstamp`, `date`, `alias`, `author`, `published`, `headline`, `teaser`, `subheadline`, `text`, `addImage`, `singleSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `source`, `jumpTo`, `url`, `target`, `caption`, `time`, `addEnclosure`, `enclosure`, `noComments`) VALUES(4, 2, 1195910396, 1195564500, 'hilary-hahn-performs-at-music-academy', '3', '1', 'Hilary Hahn performs at Music Academy', 'The Grammy-winning violinist Hilary Hahn is going to perform live at Music Academy.', 'The Grammy-winning violinist Hilary Hahn is going to perform live at Music Academy on the 20th of December 2007.', 'At the age of 27, Grammy Award-winning violinist Hilary Hahn is one of the most compelling artists on the international concert circuit. Renowned for her intellectual and emotional maturity, she was named "America''s Best" young classical musician by Time Magazine in 2001, and appears on a regular basis with the world''s great orchestras in Europe, Asia, and North America.<br />\n<br />\nWatch Hilary Hahn perform live at Music Academy on the 20th of December 2007!\n', '', '', '', '', '', '', '', 'default', 0, '', '', '', 1195564500, '', '', '');
INSERT INTO `tl_news` (`id`, `pid`, `tstamp`, `date`, `alias`, `author`, `published`, `headline`, `teaser`, `subheadline`, `text`, `addImage`, `singleSRC`, `size`, `alt`, `floating`, `imagemargin`, `fullsize`, `source`, `jumpTo`, `url`, `target`, `caption`, `time`, `addEnclosure`, `enclosure`, `noComments`) VALUES(5, 3, 1195911158, 1195911000, 'benny-green-performs-at-music-academy', '2', '1', 'Benny Green performs at Music Academy', 'The famous jazz pianist Benny Green is going to perform live at Music Academy.', 'The famous jazz pianist Benny Green is going to perform live at Music Academy on the 18th of December 2007.', 'Benny Green is a hard bop jazz pianist who "graduated" from Art Blakey''s Jazz Messengers. As a boy he grew up in Berkeley, California and studied classical piano from the age of seven. He also had an interest in jazz from an early point as his father was a jazz tenor saxophone player. Benny Green was "discovered" by Fay Carroll and later while still in his teens worked in a quintet led by Eddie Henderson.<br />\n<br />\nWatch Benny Green perform live at Music Academy on the 18th of December 2007!\n', '', '', '', '', '', '', '', 'default', 0, '', '', '', 1195911000, '', '', '');
